import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './restaurantPage.css';
import { Navbar, Nav, Card, Button, CardDeck, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const RestaurantDetail = () => {
    const { RestaurantID } = useParams();
    const [restaurant, setRestaurant] = useState({});
    const [menu, setMenu] = useState({});
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        const getRestaurant = async () => {
            try {
                const restaurantResponse = await axios.get(`http://localhost:4000/restaurant/${RestaurantID}`);
                setRestaurant(restaurantResponse.data);
                const menuResponse = await axios.get(`http://localhost:4000/restaurant/Menu/${RestaurantID}`);
                setMenu(menuResponse.data);
                const reviewsResponse = await axios.get(`http://localhost:4000/review/restaurant/${RestaurantID}`);
                setReviews(reviewsResponse.data);
            } catch (error) {
                console.error('Error fetching restaurant data:', error);
            }
        };

        getRestaurant();
    }, []);

    const handleAddToCart = async (menuItem) => {
        try {
          // Check if an order exists for the customer
          const checkOrderResponse = await axios.get(`http://localhost:4000/orders/checkOrder`, {
            params: {
              // Add parameters to identify the order, such as user ID
              // For example:
              userId: 456,
            },
          });

          if (checkOrderResponse.data.orderExists) {
            // Order already exists, add the item to the existing order
            const addToExistingOrderResponse = await axios.post(`http://localhost:4000/orders/addToExistingOrder`, {
              OrderID: checkOrderResponse.data.orderId,
              // Add other item details to the request body
              MenuItemID: menuItem.id,
              Quantity: 1, // Assuming you're adding one quantity for now
              // ... other item details
            });

            console.log(`Added ${menuItem.Name} to the existing order. Order ID: ${checkOrderResponse.data.orderId}`);
          } else {
            // Order doesn't exist, create a new order
            const createOrderResponse = await axios.post(`http://localhost:4000/orders`, {
              CustomerID: 0,
              DeliveryAddress: "saddsa",
              PaymentStatus: "sida",
              OrderStatus: "sda",
              // ... other order details
            });

            const newOrderId = createOrderResponse.data.ID;

            // Add the item to the newly created order
            const addToNewOrderResponse = await axios.post(`http://localhost:4000/orders/addToExistingOrder`, {
              OrderID: newOrderId,
              MenuItemID: menuItem.id,
              Quantity: 1, // Assuming you're adding one quantity for now
              // ... other item details
            });

            console.log(`Added ${menuItem.Name} to the new order. Order ID: ${newOrderId}`);
          }
        } catch (error) {
          console.error('Error adding to cart', error);
        }
      };


    return (
        <div>
            <Navbar bg="dark" variant="dark" className="navbar bg-dark">
                <Navbar.Brand href="/home">
                    <Button variant="secondary" className="menu-btn">Dashers</Button> 
                </Navbar.Brand>
                <Nav className="mr-auto">
                    <Nav.Link href="/order">Order</Nav.Link>
                    <Nav.Link href="/res">Restaurant</Nav.Link>
                </Nav>
            </Navbar>
            <div className="container mt-5">
                <h1 className="text-center mb-4">{restaurant.Name} Menu</h1>
                {Object.keys(restaurant).length > 0 ? (
                    <div>
                        <div className="image-container">
                            <img src={restaurant[0].Image} alt={restaurant[0].Name} className="image" />
                        </div>
                        <h1 id="Title" className="text-left mt-3">{restaurant[0].Name}</h1>
                        <h4>
                            <p>Rating: {restaurant[0].Rating}</p>
                            <p>Popular Item: {restaurant[0].Popular_Item}</p>
                        </h4>
                        <div className="menu-items mt-4">
                            {menu && menu.length > 0 ? (
                                <Row xs={1} md={3} className="g-4">
                                    {menu.map((menuItem, index) => (
                                        <Col key={index}>
                                            <Card className="mb-3">
                                                <Card.Body>
                                                    <Card.Title>{menuItem.Name}</Card.Title>
                                                    <div>
                                                        <p>{menuItem.Category}</p>
                                                        <p>Price: ${menuItem.Price}</p>
                                                        <p>Calories: {menuItem.Calories}</p>
                                                    </div>
                                                    <Button variant="primary" onClick={() => handleAddToCart(menuItem)}>
                                                        Add to Cart
                                                    </Button>
                                                </Card.Body>
                                            </Card>
                                        </Col>
                                    ))}
                                </Row>
                            ) : (
                                <p>No menu items available.</p>
                            )}
                        </div>

                        <div className="customer-reviews mt-4">
                            <h2>Customer Reviews</h2>
                            {reviews.length > 0 ? (
                                <Row xs={1} md={3} className="g-4">
                                    {reviews.map((review, index) => (
                                        <Col key={index}>
                                            <Card>
                                                <Card.Body>
                                                    <Card.Title>{review.Name}:</Card.Title>
                                                        <div>{review.Rating}/10</div>
                                                        <br></br>
                                                        <div><p id="reviewText">Customer states:</p> {review.Body}</div>
                                                </Card.Body>
                                            </Card>
                                        </Col>
                                    ))}
                                </Row>
                            ) : (
                                <p>No reviews yet.</p>
                            )}
                        </div>
                    </div>
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </div>
    );
};

export default RestaurantDetail;
