// RestaurantPage.jsx

import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const RestaurantPage = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [navbarOpen, setNavbarOpen] = useState(false);

  useEffect(() => {
    // Fetch restaurant data from the Express server
    fetch('http://localhost:5000/api/restaurants')
      .then(response => response.json())
      .then(data => setRestaurants(data))
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div>
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="/order">
          <Button variant="secondary" className="menu-btn">...</Button> Dashers
        </Navbar.Brand>
        <Nav className="mr-auto">
          <Nav.Link href="/order">Order</Nav.Link>
          <Nav.Link href="/res">Restaurant</Nav.Link>
        </Nav>
      </Navbar>

      <div className="container mt-5">
        <h1 className="text-center mb-4">Explore Restaurants</h1>

        <div className="row">
          {restaurants.map(restaurant => (
            <div key={restaurant.id} className="col-md-4 mb-4">
              {/* Use Link to navigate to the specific restaurant URI */}
              <Link to={`/restaurant/${restaurant.id}`}>
                <Card>
                  <Card.Img variant="top" src={`https://via.placeholder.com/150?text=${restaurant.name}`} />
                  <Card.Body>
                    <Card.Title>{restaurant.name}</Card.Title>
                  </Card.Body>
                </Card>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RestaurantPage;
