import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Card, Button } from 'react-bootstrap';
import './restaurantPage.css'; // Import a CSS file for custom styles
// ----------------------------------------------
// retrieve necessary files (express and cors)
const express = require("express")
const cors = require("cors")
// retrieve the MySQL DB Configuration Module
const dbConnection = require("./config")
// use this library for parsing HTTP body requests
var bodyParser = require('body-parser');


// ----------------------------------------------
// (A)  Create an express application instance
//      and parses incoming requests with JSON
//      payloads
// ----------------------------------------------
var app = express(express.json);

// ----------------------------------------------
// (B)  Use the epxress cors middleware
//      Cross-origin resource sharing (CORS)
//      is a technique that restricts specified
//      resources within web page to be accessed
//      from other domains on which the origin
//      resource was initiated the HTTP request
//      Also use the bodyParser to parse in 
//      format the body of HTTP Requests
// ----------------------------------------------
app.use(cors());
app.use(bodyParser.json());
const RestaurantPage = () => {
  const [navbarOpen, setNavbarOpen] = useState(false);

  // Sample list of restaurants
  const restaurants = [
    {
      id: 1,
      name: 'Delicious Bites',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 2,
      name: 'Spicy Noodles',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 3,
      name: 'Tasty Pizza',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 4,
      name: 'Sushi Express',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 5,
      name: 'Burger Haven',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 6,
      name: 'Veggie Delight',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 7,
      name: 'Mediterranean Flavors',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 8,
      name: 'Sweet Treats',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
    {
      id: 9,
      name: 'Coffee House',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image URL
    },
  ];

  return (
    <div>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="/order">
          <Button variant="secondary" className="menu-btn">...</Button> Dashers
        </Navbar.Brand>
        <Nav className="mr-auto">
          <Nav.Link href="/order">Order</Nav.Link>
          <Nav.Link href="/res">Restaurant</Nav.Link>
        </Nav>
      </Navbar>

      {/* Main Content */}
      <div className="container mt-5">
        <h1 className="text-center mb-4">Explore Restaurants</h1>

        <div className="row">
          {restaurants.map((restaurant) => (
            <div key={restaurant.id} className="col-md-4 mb-4">
              <Card>
                <Card.Img variant="top" src={restaurant.imageUrl} />
                <Card.Body>
                  <Card.Title>{restaurant.name}</Card.Title>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RestaurantPage;
