import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Card, Button } from 'react-bootstrap';
import './restaurantPage.css'; // Import a CSS file for custom styles
import axios from "axios";
import { useParams, useNavigate, Link } from "react-router-dom";

const RestaurantPage = () => {
  const [name, setName] = useState([]);
  useEffect(() => {
    const getNames = async () => {
      try {
        const response = await axios.get("http://localhost:4000/restaurant/chickfila", {
          //headers: { 'sort': 'population' }
        });
        setName(response.data);
      } catch (err) {
        console.log("Error: " + err)
      }
    };
    getNames();
  }, []);
  const [navbarOpen, setNavbarOpen] = useState(false);
console.log(name);
  // Sample list of restaurants

  return (
    <div>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="/order">
          <Button variant="secondary" className="menu-btn">...</Button> Dashers
        </Navbar.Brand>
        <Nav className="mr-auto">
          <Nav.Link href="/order">Order</Nav.Link>
          <Nav.Link href="/res">Restaurant</Nav.Link>
        </Nav>
      </Navbar>

      {/* Main Content */}
      <div className="container mt-5">
        <h1 className="text-center mb-4">Explore Restaurants</h1>

        <div className="row">
          {name.map((name) => (
            <div key={name[0]} className="col-md-4 mb-4">
              <Card>
                <Card.Img variant="top" src={name[3]} />
                <Card.Body>
                  <Card.Title>{name[1]}</Card.Title>
                  console.log(name[1])
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RestaurantPage;
