import React, { useState, useEffect } from 'react';

const RestaurantPage = ({ match }) => {
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate fetching restaurant data from an API
    const fetchRestaurantData = async () => {
      try {
        // Replace this with actual API call to fetch restaurant data based on the ID from the route params
        const response = await fetch(`/api/restaurants/${match.params.restaurantId}`);
        const data = await response.json();

        setRestaurant(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching restaurant data:', error);
        setLoading(false);
      }
    };

    fetchRestaurantData();
  }, [match.params.restaurantId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!restaurant) {
    return <div>Restaurant not found</div>;
  }

  return (
    <div>
      <h1>{restaurant.name}</h1>
      <p>{restaurant.description}</p>
      <p>Location: {restaurant.location}</p>
      <p>Rating: {restaurant.rating}</p>

      {/* Add more details or components as needed */}

      <div>
        <h2>Menu</h2>
        <ul>
          {restaurant.menu.map((item) => (
            <li key={item.id}>
              {item.name} - ${item.price}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RestaurantPage;
