import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './restaurantPage.css';
import { Navbar, Nav, Card, Button } from 'react-bootstrap';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';


const RestaurantDetail = () => {
    const { id } = useParams();
    const [restaurant, setRestaurants] = useState([]);

    useEffect(() => {
        const getRestaurants = async () => {
            try {
                const response = await axios.get(`http://localhost:4000/restaurant/${id}`); // Assuming your server endpoint is '/restaurants'
                setRestaurants(response.data);
                console.log(response.data);
            } catch (error) {
                console.error('Error fetching restaurant data:', error);
            }
        };

        getRestaurants();
    }, [id]);
    // Navbar state
    const [navbarOpen, setNavbarOpen] = useState(false);
    return (
        <div>
            {/* Navbar */}
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand href="/order">
                    <Button variant="secondary" className="menu-btn">...</Button> Dashers
                </Navbar.Brand>
                <Nav className="mr-auto">
                    <Nav.Link href="/order">Order</Nav.Link>
                    <Nav.Link href="/res">Restaurant</Nav.Link>
                </Nav>
            </Navbar>
            <Card className="custom-card">
              {console.log(restaurant)}
                <Card.Img variant="top" src={restaurant.Image} className="card-image"/>
                <Card.Body>
                  <Card.Title>{restaurant.Name}</Card.Title>
                  <Card.Text>Review: {restaurant.Rating}/10</Card.Text>
                  <Card.Text>Popular items: {restaurant.Popular_Item}</Card.Text>
                </Card.Body>
              </Card>
            {/* Main Content */}
            <div className="container mt-5">
                <h1 className="text-center mb-4">{restaurant.Name} Details</h1>
                <div>
                    <p>Rating: {restaurant.Rating}</p>
                    <p>Popular Item: {restaurant.Popular_Item}</p>
                </div>
            </div>
        </div>
    );
};

export default RestaurantDetail;
