import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Card, Button } from 'react-bootstrap';
import axios from 'axios';
import { Link } from 'react-router-dom';

const RestaurantPage = () => {
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    const getRestaurants = async () => {
      try {
        const response = await axios.get('http://localhost:4000/restaurant'); // Assuming your server endpoint is '/restaurants'
        setRestaurants(response.data);
      } catch (error) {
        console.error('Error fetching restaurant data:', error);
      }
    };

    getRestaurants();
  }, []);
  // Navbar state
  const [navbarOpen, setNavbarOpen] = useState(false);
  return (
    <div>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="/order">
          <Button variant="secondary" className="menu-btn">...</Button> Dashers
        </Navbar.Brand>
        <Nav className="mr-auto">
          <Nav.Link href="/order">Order</Nav.Link>
          <Nav.Link href="/res">Restaurant</Nav.Link>
        </Nav>
      </Navbar>

      {/* Main Content */}
      <div className="container mt-5">
        <h1 className="text-center mb-4">Explore Restaurants</h1>

        <div className="row">
          {restaurants.map((restaurant) => (
            <div key={restaurant.ID} className="col-md-4 mb-4">
              <img src={require('/images/mcdonald.png')} />
              <Card>
              {console.log(restaurant.Image)}
                <Card.Img variant="top" src='dashers-app/src/images/chickfila.jpeg'/>
                <Card.Body>
                  <Card.Title>{restaurant.Name}</Card.Title>
                  <Card.Text>{restaurant.Rating}</Card.Text>
                  <Card.Text>{restaurant.Popular_Item}</Card.Text>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RestaurantPage;
